shared UserQuery = let Source = #"ABS_PAR_HandyCandy2",
  #"Removed columns" = Table.RemoveColumns(Source, {"TransactionId", "StoreId"}),
  #"Added custom" = Table.AddColumn(#"Removed columns", "EndOfPreviousMonth", each Date.AddDays([TransactionDate],- Date.Day([TransactionDate]) )),
  #"Added custom 1" = Table.AddColumn(#"Added custom", "SalesMonth", each Date.AddDays([EndOfPreviousMonth], 1)),
  #"Removed columns 1" = Table.RemoveColumns(#"Added custom 1", {"EndOfPreviousMonth", "TransactionDate"}),
  #"Added custom 2" = Table.AddColumn(#"Removed columns 1", "ItemCount", each 1),
